mapper.select:
/* SQLMapId (BoardMapper.getBoardList) */ SELECT A1.SUBJECT AS SUBJECT , A1.CATGRY_ID AS CATEGORY_ID , B1.CATGRY_NAME AS CATEGORY_NAME , A1.HIT_COUNT , A1.RECMMD_COUNT AS RECOMMEND_COUNT , A1.REGT_DATE AS REGISTER_DATE , A1.REGT_USER_ID AS REGISTER_USER_ID FROM TB_BOARD_POST A1 INNER JOIN TB_BOARD_CTGRY B1 ON B1.CATGRY_ID = A1.CATGRY_ID
A1.BOARD_ID = #{conBoardId}
ORDER BY A1.REGT_DATE DESC
